mapper.select:
select * from LECTURE join category using(cg_no)
where LEC_NO= #{lecNo}
order by LEC_NO

select count(*)
from chapter
where LEC_NO = #{lecNo}

select count(*)
from VIEW_CHAPTER
where USER_NO = #{userNo} and CHAP_NO = #{chapNo}

mapper.update:
update VIEW_CHAPTER
set USER_NO = #{userNo}, CHAP_NO = #{chapNo}, RATE = #{rating}
where USER_NO = #{userNo} and CHAP_NO = #{chapNo}
